 }\n                }).catch(e => {\n            system.error(e)\n        })\n    }\n\n    // Form参数\n    const columns: ProColumns<any>[] = [\n        #foreach($column in $columns)\n            #set($javaField=$column.javaField)\n            #set($parentheseIndex=$column.columnComment.indexOf(\"（\"))\n            #if($parentheseIndex != -1)\n                #set($comment=$column.columnComment.substring(0, $parentheseIndex))\n            #else\n                #set($comment=$column.columnComment)\n            #end\n            #if($column.pk)\n            #elseif($column.list && $column.htmlType == \"datetime\")\n                {\n                    title: \'${comment}\', ellipsis: true, width: 200, valueType: \"dateTime\", dataIndex: \'${javaField}\',\n                },\n            #elseif($column.list && \"\" != $javaField)\n                {\n                    title: \'${comment}\', ellipsis: true, width: 200, valueType: \"text\", dataIndex: \'${javaField}\',\n                },\n            #end\n        #end\n        {\n            title: \'操作\',\n            fixed: \'right\',\n            width: 320,\n            valueType: \"option\",\n            dataIndex: \'operation\',\n            render: (_, row) => {\n                return (\n                    <>\n                        <a onClick={() => refPut(row)}>\n                            <Space>\n                                <EditOutlined/>\n                                修改\n                            </Space>\n                        </a>\n\n                        <Divider type=\"vertical\"/>\n\n                        <Popconfirm\n                            onConfirm={() => refRemove(row.${pkColumn.javaField})}\n                            title=\"确定删除吗\"\n                        >\n                            <a href=\'#\'>\n                                <Space>\n                                    <CloseOutlined/>\n                                    删除\n                                </Space>\n                            </a>\n                        </Popconfirm>\n                    </>\n                )\n            }\n        },\n    ]\n\n    return (\n        <PageContainer>\n            <ProTable\n                {...proTableConfigs}\n                pagination={{\n                    // 是否允许每页大小更改\n                    showSizeChanger: true,\n                    // 每页显示条数\n                    pageSize: state.pageSize,\n                }}\n                actionRef={acForm}\n                formRef={formRef}\n                rowKey=\"${pkColumn.javaField}\"\n                columns={columns}\n                request={async (params) => {\n                    const {data} = await list${BusinessName}(params);\n                    const {records, total} = data;\n                    return Promise.resolve({\n                        data: records,\n                        success: true,\n                        total,\n                    });\n                }}\n                rowSelection={{}}\n                beforeSearchSubmit={(params) => {\n                    // 分隔搜索参数\n                    if (params.between) {\n                        const {between} = params\n                        // 移除参数\n                        delete params.between\n\n                        // 适配参数\n                        params.beginTime = between[0]\n                        params.endTime = between[1]\n                    }\n                    return params\n                }}\n                toolBarRender={(action, {selectedRowKeys}) => [\n                    <Button key=\'add\' type=\"default\" onClick={refPost}>\n                        <PlusOutlined/>\n                        新增\n                    </Button>,\n                    <Popconfirm\n                        key=\'batchDelete\'\n                        disabled={!(selectedRowKeys && selectedRowKeys.length > 0)}\n                        onConfirm={() => refRemove(selectedRowKeys)}\n                        title=\"是否删除选中数据\"\n                    >\n                        <Button\n                            disabled={!(selectedRowKeys && selectedRowKeys.length > 0)}\n                            type=\"primary\" danger\n                        >\n                            <DeleteOutlined/>\n                            批量删除\n                        </Button>\n                    </Popconfirm>,\n                    <Popconfirm\n                        key=\'export\'\n                        title=\"是否导出数据\"\n                        onConfirm={() => {\n                            export${BusinessName}({\n                                ...formRef.current?.getFieldsValue()\n                            })\n                        }}\n                    >\n                        <Button type=\"default\">\n                            <FundProjectionScreenOutlined/>\n                            导出数据\n                        </Button>\n                    </Popconfirm>\n                ]}\n\n            />\n\n            <Modal\n                title={`${modal.title}${functionName}`}\n                open={modal.visible}\n                okText={`${modal.title}`}\n                confirmLoading={loadingModal}\n                onOk={onSave}\n                onCancel={handleCancel}\n                // 销毁组件，要求重新装载\n                destroyOnClose\n            >\n\n                <Form\n                    form={form}\n                >\n                    <Form.Item\n                        hidden\n                        {...formItemLayout}\n                        label=\"主键\"\n                        name=\"${pkColumn.javaField}\"\n                        initialValue={0}\n                    >\n                        <Input/>\n                    </Form.Item>\n                    #foreach($column in $columns)\n                        #set($field=$column.javaField)\n                        #if($column.insert && !$column.pk)\n                            #if(($column.usableColumn) || (!$column.superColumn))\n                                #if($column.isRequired)\n                                    #set($isRequired=true)\n                                #else\n                                    #set($isRequired=false)\n                                #end\n\n                                #set($parentheseIndex=$column.columnComment.indexOf(\"（\"))\n                                #if($parentheseIndex != -1)\n                                    #set($comment=$column.columnComment.substring(0, $parentheseIndex))\n                                #else\n                                    #set($comment=$column.columnComment)\n                                #end\n                                #set($dictType=$column.dictType)\n                                #if(\"\" != $treeParentCode && $column.javaField == $treeParentCode)\n                                    <Form.Item\n                                        {...formItemLayout}\n                                        label=\"${comment}\"\n                                        rules={[{required: ${isRequired}, message: \'${comment}不能为空\'}]}\n                                        name=\"${field}\"\n                                    >\n                                        <TreeSelect\n                                            placeholder={\"请选择${comment}\"}\n                                            showSearch\n                                            treeLine\n                                            treeNodeFilterProp=\"title\"\n                                            treeData={${businessName}Options}\n                                        />\n                                    </Form.Item>\n                                #elseif($column.htmlType == \"input\")\n                                    <Form.Item\n                                        {...formItemLayout}\n                                        label=\"${comment}\"\n                                        rules={[{required: ${isRequired}, message: \'${comment}不能为空\'}]}\n                                        name=\"${field}\"\n                                    >\n                                        <Input placeholder={\"请输入${comment}\"}/>\n                                    </Form.Item>\n                                #elseif($column.htmlType == \"imageUpload\")\n                                    <Form.Item\n                                        {...formItemLayout}\n                                        label=\"${comment}\"\n                                        rules={[{required: ${isRequired}, message: \'请上传${comment}\'}]}\n                                        name=\"${field}\"\n                                    >\n                                        <UploadTWT\n                                            name=\'file\'\n                                            maxCount={1}\n                                            // 开启图片剪裁\n                                            imgCrop={true}\n                                            action={TWT.uploadUrl}\n                                        />\n                                    </Form.Item>\n                                #elseif($column.htmlType == \"fileUpload\")\n                                    <Form.Item\n                                        {...formItemLayout}\n                                        label=\"${comment}\"\n                                        rules={[{required: ${isRequired}, message: \'请上传${comment}\'}]}\n                                        name=\"${field}\"\n                                    >\n                                        <UploadTWT\n                                            name=\'file\'\n                                            maxCount={1}\n                                            listType={\'picture\'}\n                                            action={TWT.uploadUrl}\n                                            title={<Button> 上传文件</Button>}\n                                        />\n                                    </Form.Item>\n                                #elseif($column.htmlType == \"editor\")\n                                    <Form.Item\n                                        {...formItemLayout}\n                                        label=\"${comment}\"\n                                        rules={[{required: ${isRequired}, message: \'${comment}不能为空\'}]}\n                                        name=\"${field}\"\n                                    >\n                                        <Upload\n                                            name=\'file\'\n                                            headers={{\n                                                Authorization: `Bearer ${token.access_token}`\n                                            }}\n                                            maxCount={1}\n                                        >\n                                            <Button>上传文件</Button>\n                                        </Upload>\n                                    </Form.Item>\n                                #elseif($column.htmlType == \"select\" && $dictType)\n                                    <Form.Item\n                                        {...formItemLayout}\n                                        label=\"${comment}\"\n                                        rules={[{required: ${isRequired}, message: \'${comment}不能为空\'}]}\n                                        name=\"${field}\"\n                                    >\n                                        <DictionariesSelect type=\'${dictType}\' />\n                                    </Form.Item>\n                                #elseif($column.htmlType == \"checkbox\" && $dictType)\n                                    <Form.Item\n                                        // 格式数组\n                                        getValue